def ProcuraS(input):
    for i in range(len(input[0])):
        inicio = input[0].find('S')
        if inicio != -1:
            return inicio
    return None

def funcaoRandom():

    # indexInicio = ProcuraS(input)
    qntsVezesDividiu = 0

    for i in range(len(input)):
        for j in range(len(input[i])):

            if input[i][j] == '.':
                input[i][j] = '|'

            elif input[i][j] == '^':
                qntsVezesDividiu += 1
                input[i][j-1] = '|'
                input[i][j+1] = '|'

    print(qntsVezesDividiu)
    
    

